
public class PROJECT25 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		long l = 62;
		int i = 33;
		short s = 78;
		byte b = 33;
		i = (int) l;
		s = (short) i;
		b = (byte) s;
		System.out.println("l = " + l);
		System.out.println("i = " + i);
		System.out.println("s = " + s);
		System.out.println("b = " + b);
	}

}
